INSERT INTO songs (release_date, text, link, song, group_id) VALUES
    ('1998-01-01', 'Замученный дорогой я выбился из сил\nИ в доме лесника я ночлега попросил\nС улыбкой добродушной старик меня пустил\nИ жестом дружелюбным на ужин пригласил\n\nХэй!\nБудь как дома, путник\nЯ ни в чём не откажу\nЯ ни в чём не откажу\nЯ ни в чём не откажу! Хэй!\nМножество историй\nКоль желаешь — расскажу\nКоль желаешь — расскажу\nКоль желаешь — расскажу!\n\nНа улице темнело, сидел я за столом\nЛесник сидел напротив, болтал о том, о сём\nЧто нет среди животных у старика врагов\nЧто нравится ему подкармливать волков\n\nБудь как дома, путник\nЯ ни в чём не откажу\nЯ ни в чём не откажу\nЯ ни в чём не откажу! Хэй!\nМножество историй\nКоль желаешь — расскажу\nКоль желаешь — расскажу\nКоль желаешь — расскажу!\n\nИ волки среди ночи завыли под окном\nСтарик заулыбался и вдруг покинул дом\nНо вскоре возвратился с ружьём наперевес\n\"Друзья хотят покушать, пойдём, приятель, в лес!\"\n\nБудь как дома, путник\nЯ ни в чём не откажу\nЯ ни в чём не откажу\nЯ ни в чём не откажу! Хэй!\nМножество историй\nКоль желаешь — расскажу\nКоль желаешь — расскажу\nКоль желаешь — расскажу!', 'https://youtu.be/i7R6_7e10QM', 'Лесник', 1),
    ('2000-03-15', 'Проныра озорник, любитель книг\nЛовкач игрок, жизнь между строк\nИ потому открыт ему\nНезримый путь в любую суть\n\nТанец злобного гения\nНа страницах произведения\nЭто игра без сомнения\nОбречённых ждёт поражение\n\nПодсыпать в душу яд всегда он рад\nВсего за час прочтёт он вас\nОн волен взять и поменять\nСтроку, и с ней смысл темы всей. Хоу!\n\nТанец злобного гения\nНа страницах произведения\nЭто игра без сомнения\nОбречённых ждёт поражение. Хоу!\n\nОткрыт роман, читатель пьян\nРазлив вино, шагнул в окно\n\nТанец злобного гения\nНа страницах произведения\nЭто игра без сомнения\nОбречённых ждёт поражение\n\nТанец злобного гения\nНа страницах произведения\nЭто игра без сомнения\nОбречённых ждёт поражение', 'https://youtu.be/NtQGTCnclQ4', 'Танец злобного гения', 1),
    ('2002-07-12', 'Тёмный, мрачный коридор\nЯ на цыпочках, как вор\nПробираюсь, чуть дыша\nЧтобы не спугнуть\nТех, кто спит уже давно\nТех, кому не всё равно\nВ чью я комнату тайком\nЖелаю заглянуть\nЧтобы увидеть...\n\nКак бессонница в час ночной\nМеняет, нелюдимая, облик твой\nЧьих, невольница, ты идей?\nЗачем тебе охотиться на людей?\n\nКрестик на моей груди\nНа него ты погляди\nЧто в тебе способен он\nРезко изменить?\nМного книжек я читал\nМного фокусов видал\nСвою тайну от меня\nНе пытайся скрыть!\nЯ это видел!\n\nКак бессонница в час ночной\nМеняет, нелюдимая, облик твой\nЧьих, невольница, ты идей?\nЗачем тебе охотиться на людей?\n\nОчень жаль, что ты тогда\nМне поверить не смогла\nВ то, что новый твой приятель\nНе такой, как все!\nТы осталась с ним вдвоём\nНе зная ничего о нём\nЧто для всех опасен он\nНаплевать тебе!\nИ ты попала!\n\nК настоящему колдуну\nОн загубил таких, как ты, не одну!\nСловно куклой, в час ночной\nТеперь он может управлять тобой!\n\nВсё происходит, будто в страшном сне\nИ находиться здесь опасно мне!', 'https://youtu.be/yUp01GbQxTw', 'Кукла колдуна', 1);
INSERT INTO songs (release_date, text, link, song, group_id) VALUES
    ('1996-03-30', 'Im the trouble starter, punkin instigator\nIm the fear addicted, danger illustrated (hey, hey, hey)\nIm a firestarter, twisted firestarter (hey, hey, hey)\nYoure a firestarter, twisted firestarter (hey, hey, hey)\nIm a firestarter, twisted firestarter\n\nIm the bitch you hated, filth infatuated, yeah\nIm the pain you tasted, fell intoxicated (hey, hey, hey)\nIm a firestarter, twisted firestarter (hey, hey, hey)\nYoure a firestarter, twisted firestarter (hey, hey, hey)\n\nI''m the self inflicted, mind detonator, yeah\nIm the one infected, twisted animator (hey, hey, hey)\nIm a firestarter, twisted firestarter (hey, hey, hey)\nYoure a firestarter, twisted firestarter (hey, hey, hey)\nIm a firestarter, twisted firestarter starter', 'https://youtu.be/wmin5WkOuPw', 'Firestarter', 2),
