INSERT INTO songs (release_date, text, link, song, group_id) VALUES
    ('1985-12-12', 'Этот парень был из тех\nКто просто любит жизнь\nЛюбит праздники и громкий смех\nПыль дорог и ветра свист\nОн был везде и всегда своим\nВлюблял в себя целый свет\nИ гнал свой байк, а не лимузин\nТаких друзей больше нет\n\nИ в гостиной при свечах\nОн танцевал, как Бог\nНо зато менялся на глазах\nТолько вспомнит шум дорог\nВсё, что имел, тут же тратил\nИ за порог сделав шаг\nМой друг давал команду братьям\nВверх поднимая кулак\n\nТы летящий вдаль, вдаль ангел\nТы летящий вдаль, вдаль ангел\nТы один только друг, друг на все времена\nНе много таких среди нас\nТы летящий вдаль беспечный ангел\n\nПод гитарный жёсткий рок\nКоторый так любил\nНа Харлее он домчать нас мог\nДо небес и звёзд любых\nНо он исчез, и никто не знал\nКуда теперь мчит его байк\nОдин бродяга нам сказал\nЧто он отправился в рай\n\nТы летящий вдаль, вдаль ангел\nТы летящий вдаль, вдаль ангел\nНо ад стал союзником рая в ту ночь\nПротив тебя одного\nТы летящий вдаль беспечный ангел\n\nТы летящий вдаль, вдаль ангел\nТы летящий вдаль, вдаль ангел\nНо ад стал союзником рая в ту ночь\nПротив тебя одного\nТы летящий вдаль, вдаль ангел\nТы летящий вдаль беспечный ангел', 'https://youtu.be/ztKl8qqZr0c', 'Беспечный ангел', 3),
    ('1987-05-19', 'Твой дом стал для тебя тюрьмой\nДля тех, кто в доме, ты чужой\nТы был наивен и ждал перемен\nТы ждал, что друг тебя поймёт\nПоймёт и скажет: "Жми вперёд!"\nНо друг блуждал среди собственных стен\n\nГорел асфальт\nОт солнца и от звёзд\nГорел асфальт\nПод шум колёс\nГорел асфальт\nТы чувствовал тепло\nГорел асфальт\nСнегам назло\n\nТы сам решил пойти на риск\nНикто не крикнул: "Берегись!"\nИ ты покрасил свой шлем в чёрный цвет\nКак зверь, мотор в ночи ревёт\nПустырь, разъезд и разворот\nТы мстил за груз нелюбви прошлых лет\n\nЗапел асфальт\nТы слышал каждый звук\nЗапел асфальт\nКак сердца стук\nЗапел асфальт\nТы был его герой\nТак пел асфальт\nПел за спиной\n\nБыл миг – ты верил в знак удач\nВедь ты был молод и горяч\nНо твой двойник мчал навстречу тебе\nОн был свободен, как и ты\nНикто не крикнул: "Тормози!"\nТакой приказ неизвестен судьбе\n\nГорел асфальт\nОт сбитых с неба звёзд\nГорел асфальт\nПод шум колёс\nКричал асфальт\nТы был его герой\nКричал асфальт\nКричала боль\n\nГорел асфальт\nОт сбитых с неба звёзд\nГорел асфальт\nПод шум колёс\nКричал асфальт\nТы был его герой\nКричал асфальт\nКричала боль', 'https://youtu.be/eg-DymfjDlw', 'Герой асфальта', 3),
    ('1989-08-25', 'Надо мною тишина, небо полное дождя\nДождь проходит сквозь меня, но боли больше нет\nПод холодный шёпот звёзд мы сожгли последний мост\nИ всё в бездну сорвалось. Свободным стану я от зла и от добра\nМоя душа была на лезвии ножа\n\nЯ бы мог с тобою быть, я бы мог про всё забыть\nЯ бы мог тебя любить, но это лишь игра\nВ шуме ветра за спиной я забуду голос твой\nИ о той любви земной, что нас сжигала в прах, и я сходил с ума\nВ моей душе нет больше места для тебя\n\nЯ свободен, словно птица в небесах!\nЯ свободен, я забыл, что значит страх!\nЯ свободен, с диким ветром наравне!\nЯ свободен наяву, а не во сне!\n\nНадо мною тишина, небо полное огня\nСвет проходит сквозь меня, и я свободен вновь\nЯ свободен от любви, от вражды и от молвы\nОт предсказанной судьбы и от земных оков, от зла и от добра\nВ моей душе нет больше места для тебя\n\nЯ свободен, словно птица в небесах!\nЯ свободен, я забыл, что значит страх!\nЯ свободен, с диким ветром наравне!\nЯ свободен наяву, а не во сне!\n\nЯ свободен, словно птица в небесах!\nЯ свободен, я забыл, что значит страх!\nЯ свободен, с диким ветром наравне!\nЯ свободен наяву, а не во сне!\n\nЯ свободен, словно птица в небесах!\nЯ свободен, я забыл, что значит страх!\nЯ свободен, с диким ветром наравне!\nЯ свободен наяву, а не во сне!', 'https://youtu.be/QXDRPtufEbA', 'Я свободен', 3);
